  // 主卦编号 = (上卦编号 -1)*8 + 下卦编号
  // 八卦编号1~8，64卦编号1~64
  function getMainHexNumber(upperNum, lowerNum){
    return (upperNum - 1) * 8 + lowerNum;
  }

  // 动爻变卦计算：第 movingYao 爻由阳变阴或阴变阳，结果变卦序号
  // 这里用简单位运算处理爻变
  // 卦爻自下而上，从第1爻到第6爻，二进制表示阳（1）阴（0）
  // 64卦对应的二进制码参考八卦组合（不完全准确，示例用）
